mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from dish

    name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc

select * from dish

    and name = #{name}
    and category_id = #{categoryId}
    and price = #{price}
    and image = #{image}
    and description = #{description}
    and status = #{status}
    and update_time = #{updateTime}
    and update_user = #{updateUser}

order by create_time desc
